// If either of us are home, someone is home; otherwise neither of us are home
msg.payload = global.get("state").isNickHome.value || global.get("state").isCarolineHome.value
return msg;

// If everyone is asleep true otherwise false
msg.payload = global.get("state").isMasterAsleep.value && global.get("state").isGuestAsleep.value
return msg;

// If we don't have guests, drive this just based on master
if(global.get("state").isMasterAsleep.value && (global.get("state").isHaveGuests.value == false)) {
    msg.payload = true
    return msg;
}
return null

// Check if guests currently considered asleep
if(global.get("state").isGuestAsleep.value) {
    // They are, nothing to change here
    return null
}
// Do we even have a guest right now?
if(global.get("state").isHaveGuests.value == false) {
    // Nope! stop doing this
    return null
}
// Didn't disqualify this, it's what we were waiting for
return msg

// Was master asleep?
if(global.get("state").isMasterAsleep.value) {
    // They were, now the door is open though
    return msg
}

// Was guest asleep?
if(global.get("state").isGuestAsleep.value) {
    // They were, now the door is open though
    return msg
}

// If we don't have guests, their asleep status should match master
if (global.get("state").isHaveGuests.value == false) {
    msg.payload = global.get("state").isMasterAsleep.value
    return msg;
}

if (global.get("state").isAnyoneHome.value == false) {
    return null
}
if (global.get("state").isEveryoneAsleep.value == true) {
    return null
}
return msg;

if (global.get("state").isAnyoneHome.value == false) {
    return msg
}
// Somebody is home, do nothing
return null

hueConfig = global.get("state").hueConfig.value

sunevent = global.get("state").sunevent.value

if (hueConfig.phases.indexOf(sunevent) > -1) {
    hueConfig.rooms.forEach(function(thisRoomToActivate) {
        // Does this participant even have this attribute?
        if ("off_if_true" in thisRoomToActivate) {
            // If so, is it true?
            if (global.get("state")[thisRoomToActivate.off_if_true].value) {
                return null
            }
        }

        msg.payload = {}
        msg.payload.group_name = thisRoomToActivate.hue_group
        msg.payload.scene_name = sunevent
        msg.payload.dynamic = true
        // The nook doesn't do well with dynamics b/c of its lights
        if (thisRoomToActivate.hue_group == "Nook") {
            msg.payload.dynamic = false
        }
        node.send(msg)
    });
} else {
    node.error("sunevent (" + sunevent +") not in hueConfig")
    return null
}

hueConfig = global.get("state").hueConfig.value

sunevent = global.get("state").sunevent.value

if (hueConfig.phases.indexOf(sunevent) > -1) {
    hueConfig.rooms.forEach(function(thisRoomToTurnOff) {
        // Does this participant even have this attribute?
        if ("off_if_true" in thisRoomToTurnOff) {
            // Is the variable change triggering us the special "reset" case, or is it the variable this room cares about?
            if ((msg.topic == "reset" || msg.topic == thisRoomToTurnOff.off_if_true) 
            // Is the variable this room cares about true?
            && global.get("state")[thisRoomToTurnOff.off_if_true].value) {
                msg.targetArea = thisRoomToTurnOff.hass_area_id
                node.send(msg)
            }
        }
    });
} else {
    node.error("sunevent (" + sunevent +") not in hueConfig")
    node.error(hueConfig.phases)
    return null
}

hueConfig = global.get("state").hueConfig.value

hueConfig.rooms.forEach(function(thisRoomToTurnOff) {
    // Pick every single room
    msg.targetArea = thisRoomToTurnOff.hass_area_id
    node.send(msg)
});

// Music type is set by variable
musicType = global.get("state").musicPlaybackType.value
// We use an object variable or tracking which # playlist in the type
if (typeof global.get("state").musicPlaylistNumbers != "undefined") {
    musicPlaylistNumbers = global.get("state").musicPlaylistNumbers.value
} else {
    musicPlaylistNumbers = {}
}
// If this type has a number already, use it; otherwise create it as 0
if (musicType in musicPlaylistNumbers) {
    thisPlaylistNumber = musicPlaylistNumbers[musicType]
} else {
    thisPlaylistNumber = musicPlaylistNumbers[musicType] = 0
}

// Get full musicConfig
musicConfig = global.get("state").musicConfig.value
// Get the overall object for this type of music
musicTypeObject = musicConfig[musicType]
// Get the specifics for this playback option of the music type
musicToPlay = musicTypeObject["playback_options"][thisPlaylistNumber]
// Generate a list of players to group for playback based on the config
players = ""
// Iterate over all participants in this music playback type
musicTypeObject["participants"].forEach(function(participant, index) {
    // Take the name of this player participants and add it to the list of all participants
    players = players + participant.player_name + ","
    // At the level of the music type we define criteria
    // This information needs to be added to playback profile including a specific volume
    musicToPlay["participants"].forEach(function(playbackParticipant) {
        if (playbackParticipant.player_name == participant.player_name) {
            // Take criteria global for participant in musicType and apply
            playbackParticipant.leave_muted_if = participant.leave_muted_if
            // Determine appropriate volume for this participant using:
            //  * default from playback type for this speaker
            //  * multiplier for this playback option
            playbackParticipant.volume = Math.round(participant.default_volume * musicToPlay.volume_multiplier)
        }
    });
    // Mute all players being engaged in this group
    muteMsg = {}
    muteMsg.payload = "on"
    muteMsg.playerName = participant.player_name
    node.send([null, null, muteMsg, null])
});
// Strip last comma
players = players.replace(/,$/, ''); 

// Get lead player
leadPlayerName = musicToPlay.participants[0].player_name

// Playback is initiated by building the group and providing instructions to lead player
playMsg = {}
playMsg.payload = players
playMsg.playerName = leadPlayerName
playMsg.command = musicToPlay.command 
playMsg.uri = musicToPlay.uri

currentlyPlayingMusic = {}
currentlyPlayingMusic.payload = musicToPlay
currentlyPlayingMusic.payload.leadPlayer = leadPlayerName

// Need a new playlist numbers object
incrementMsg = {}
// If this playlist number for the given type of music would exceed the number of options, roll it over
if ((musicPlaylistNumbers[musicType] + 1) >= musicConfig[musicType]["playback_options"].length) {
    musicPlaylistNumbers[musicType] = 0
} else {
    // Otherwise increment it
    musicPlaylistNumbers[musicType] = musicPlaylistNumbers[musicType] + 1
}
incrementMsg.payload = musicPlaylistNumbers

return [ playMsg, currentlyPlayingMusic, null, incrementMsg ]

// By default, move to set volume and appropriately unmute all players in current playback
targets = global.get("state").currentlyPlayingMusic.value.participants
// If we were given a single specific target though, downscope
if ("target" in msg) {
    specificTarget = []
    targets.forEach(function(participant) {
        if (participant.player_name == msg.target) {
            specificTarget = [participant]
        }
    });
    if (specificTarget.length == 0) {
        node.warn("Asked to reset volume and mute state of unrecognized player: " + msg.target)
        return null
    }
    targets = specificTarget
}

// Iterate over our targets and kick off setting their volume and mute state
targets.forEach(function(target) {
    participantSpecificMsg = {}
    participantSpecificMsg.playerName = target.player_name
    participantSpecificMsg.payload = target.volume
    if ("leave_muted_if" in target) {
        participantSpecificMsg.leave_muted_if = target.leave_muted_if
    }
    node.send(participantSpecificMsg);
});

return null

stopMsg = {}
stopMsg.playerName = global.get("state").currentlyPlayingMusic.value.leadPlayerName

musicPlaybackType = global.get("state").musicPlaybackType.value

// If it's something other than none/nothing
if (musicPlaybackType) {
    return [ msg, stopMsg ]
}
// Otherwise just stop current playback
return [ null, stopMsg ]

// Only play music if someone is home
if (global.get("state").isAnyoneHome.value == false) {
    msg.payload = ""
    return msg
}
// If master is asleep, set to sleep
if (global.get("state").isMasterAsleep.value) {
    msg.payload = "sleep"
    return msg
}

// If it's day time
if (global.get("state").sunevent.value == "day" || global.get("state").sunevent.value == "morning") {
    // If what changed was someone waking up, kick off some music
    if (msg.topic == "isMasterAsleep" && msg.payload == false) {
        msg.payload = "morning"
        return msg
    }
    // If everyone isn't asleep then day starts
    if (global.get("state").isEveryoneAsleep.value == false) {
        msg.payload = "day"
        return msg
    }
// If it's sunset
} else if (global.get("state").sunevent.value == "sunset"
        || global.get("state").sunevent.value == "dusk") {
    msg.payload = "evening"
    return msg
} else if (global.get("state").sunevent.value == "night") {
    msg.payload = "night"
    return msg
}

if (global.get("state").isAnyoneHome.value == false) {
    msg.payload = false
    return msg
}
// Somebody is home, do nothing
return null

playerNames = new Set()

musicConfig = global.get("state").musicConfig.value

Object.values(musicConfig).forEach(function(musicType) {
    musicType.participants.forEach(function(participant) {
        playerNames.add(participant.player_name)
    });
});

playerNames.forEach(function(playerName) {
    msg = {}
    msg.playerName = playerName
    node.send(msg);
});

msg.payload.forEach(function(participant) {
    msg.payload = null
    msg.playerName = participant.playerName
    if ([ "Living Room", "Guest Bathroom", "Guest Bedroom" ].indexOf(msg.playerName) > -1) {
        node.send(msg)
    }
});

// Does this participant even have this attribute?
if ("leave_muted_if" in msg) {
    // Iterate over all criteria
    for (const criteria of msg.leave_muted_if) {
        // Does the value of the variable match the criteria?
        if (global.get("state")[criteria.variable].value == criteria.value) {
            // Mute this player
            msg.payload = "on"
            return msg
        }
    }
}
// Unmute this player
msg.payload = "off"
return msg

// Get the participants in the currently playing music, which are our active players
activePlayers = global.get("state").currentlyPlayingMusic.value.participants

// For each of the active players, check if the changed variable is the condition
activePlayers.forEach(function(participant) {
    // Players may not even have a criteria
    if ("leave_muted_if" in participant) {
        // Iterate over all criteria
        participant.leave_muted_if.forEach(function(criteria) {
            // This criteria is the one that changed
            if (msg.topic == criteria.variable) {
                newMsg = {}
                newMsg.target = participant.player_name
                node.send(newMsg)
            }
        });
    }
});

return null

// If this was anything BUT the pooper's button
if(msg.payload.event.id != "poopers_button_button") {
    // Do nothing
    return null
}
// If this was anything BUT a short release
if(msg.payload.event.type != "short_release") {
    // Do nothing
    return null
}

// Ok, it was a short release of the pooper's button
return msg

currentMusicConfig = global.get("state").musicConfig.value
newMusicConfig = msg.payload.music

if (Object.keys(newMusicConfig).length < Object.keys(currentMusicConfig).length) {
    node.error("New music config is less complex than current; refusing to set it")
    return null
}

msg.payload = newMusicConfig
return msg;

// If sunset
if (
    msg.payload.sunevent == "goldenHour" ||
    msg.payload.sunevent == "sunsetStart" ||
    msg.payload.sunevent == "sunset"
  ) {
    // output true
    msg.payload = "sunset"
}
// If dusk
else if (
    msg.payload.sunevent == "dusk" ||
    msg.payload.sunevent == "nauticalDusk"
  ) {
    // output true
    msg.payload = "dusk"
}
// If night
else if (
    msg.payload.sunevent == "night" ||
    msg.payload.sunevent == "nightEnd" ||
    msg.payload.sunevent == "nauticalDawn" ||
    msg.payload.sunevent == "dawn" ||
    msg.payload.sunevent == "nadir"
  ) {
    // output true
    msg.payload = "night"
}
// If morning
else if (
    msg.payload.sunevent == "sunrise" ||
    msg.payload.sunevent == "sunriseEnd" ||
    msg.payload.sunevent == "goldenHourEnd"
  ) {
    // output true
    msg.payload = "morning"
}
// If it's not sunrise, sunset, or dusk assume it's night
else {
    msg.payload = "day"
}

// Check if this is new, if it is send it
if (global.get("state").sunevent.value != msg.payload) {
    return msg
}
return null

msg.payload = (100-(msg.payload[0]['main_brush_work_time']/1080000*100)).toFixed(1);

node.status({fill:"grey",shape:"ring",text:msg.payload+'%'})


msg.payload = {
    "FilterChangeIndication":msg.payload<20?1:0,
    "FilterLifeLevel":msg.payload
}

return msg;

msg.payload = (100-(msg.payload[0]['side_brush_work_time']/720000*100)).toFixed(1);

node.status({fill:"grey",shape:"ring",text:msg.payload+'%'})
msg.payload = {
    "FilterChangeIndication":msg.payload<20?1:0,
    "FilterLifeLevel":msg.payload
}

return msg;

msg.payload = (100-(msg.payload[0]['filter_work_time']/540000*100)).toFixed(1);

node.status({fill:"grey",shape:"ring",text:msg.payload+'%'})
msg.payload = {
    "FilterChangeIndication":msg.payload<20?1:0,
    "FilterLifeLevel":msg.payload
}


return msg;

msg.payload = (100-(msg.payload[0]['sensor_dirty_time']/108000*100)).toFixed(1);

node.status({fill:"grey",shape:"ring",text:msg.payload+'%'})
msg.payload = {
    "FilterChangeIndication":msg.payload<20?1:0,
    "FilterLifeLevel":msg.payload
}


return msg;

if (global.get("state").isAnyoneHome.value == false) {
    return msg
}
// Somebody is home, do nothing
return null

msg = {}
msg.payload = Date.now()
return msg

lastVaccuumRunTimestamp = global.get("state").lastVacuumingTimestamp.value

timeSinceLastRun = Date.now() - lastVaccuumRunTimestamp

// Check against constant threshold in seconds
// 39600000 == 11 hours
if (timeSinceLastRun > 39600000) {
    // It's been too long
    return msg
}

if (global.get("state").isAnyoneHome.value == true) {
    return msg
}
// Somebody is home, do nothing
return null

// If nobody's home just go for it
if (global.get("state").isAnyoneHome.value == false) {
    return msg
}

// If we have guests, bail out
if (global.get("state").isHaveGuests.value == true) {
    return null;
}

// If we have master is not asleep, bail out
if (global.get("state").isMasterAsleep.value == false) {
    return null;
}

// Either nobody's here, or if they are it's just master and NO guests

return msg

if (global.get("state").isEveryoneAsleep.value == false) {
    return msg
}
// Somebody is home, do nothing
return null
