// Music type is set by variable
musicType = global.get("state").musicPlaybackType.value
// We use an object variable or tracking which # playlist in the type
if (typeof global.get("state").musicPlaylistNumbers != "undefined") {
    musicPlaylistNumbers = global.get("state").musicPlaylistNumbers.value
} else {
    musicPlaylistNumbers = {}
}
// If this type has a number already, use it; otherwise create it as 0
if (musicType in musicPlaylistNumbers) {
    thisPlaylistNumber = musicPlaylistNumbers[musicType]
} else {
    thisPlaylistNumber = musicPlaylistNumbers[musicType] = 0
}

// Get full musicConfig
musicConfig = global.get("state").musicConfig.value
// Get the overall object for this type of music
musicTypeObject = musicConfig[musicType]
// Get the specifics for this playback option of the music type
musicToPlay = musicTypeObject["playback_options"][thisPlaylistNumber]
// Generate a list of players to group for playback based on the config
players = ""
// Blanket copy the music type's definition of participants onto the individual playback option
musicToPlay["participants"] = musicTypeObject["participants"]
// Iterate over all participants in this music playback type
musicTypeObject["participants"].forEach(function(participant, index) {
    // Take the name of this player participants and add it to the list of all participants
    players = players + participant.player_name + ","
    // The specific playback option's volume varies based on a multiplier
    musicToPlay["participants"].forEach(function(playbackParticipant) {
        if (playbackParticipant.player_name == participant.player_name) {
            // Determine appropriate volume for this participant using:
            //  * default from playback type for this speaker
            //  * multiplier for this playback option
            playbackParticipant.volume = Math.round(participant.default_volume * musicToPlay.volume_multiplier)
        }
    });
    // Mute all players being engaged in this group
    muteMsg = {}
    muteMsg.payload = "on"
    muteMsg.playerName = participant.player_name
    node.send([null, null, muteMsg, null])
});
// Strip last comma
players = players.replace(/,$/, ''); 

// Get lead player
leadPlayerName = musicToPlay.participants[0].player_name

// Playback is initiated by building the group and providing instructions to lead player
playMsg = {}
playMsg.payload = players
playMsg.playerName = leadPlayerName
playMsg.command = musicToPlay.command 
playMsg.uri = musicToPlay.uri

currentlyPlayingMusic = {}
currentlyPlayingMusic.payload = musicToPlay
currentlyPlayingMusic.payload.leadPlayer = leadPlayerName

// Need a new playlist numbers object
incrementMsg = {}
// If this playlist number for the given type of music would exceed the number of options, roll it over
if ((musicPlaylistNumbers[musicType] + 1) >= musicConfig[musicType]["playback_options"].length) {
    musicPlaylistNumbers[musicType] = 0
} else {
    // Otherwise increment it
    musicPlaylistNumbers[musicType] = musicPlaylistNumbers[musicType] + 1
}
incrementMsg.payload = musicPlaylistNumbers

return [ playMsg, currentlyPlayingMusic, null, incrementMsg ]

// By default, move to set volume and appropriately unmute all players in current playback
targets = global.get("state").currentlyPlayingMusic.value.participants
// If we were given a single specific target though, downscope
if ("target" in msg) {
    specificTarget = []
    targets.forEach(function(participant) {
        if (participant.player_name == msg.target) {
            specificTarget = [participant]
        }
    });
    if (specificTarget.length == 0) {
        node.warn("Asked to reset volume and mute state of unrecognized player: " + msg.target)
        return null
    }
    targets = specificTarget
}

// Iterate over our targets and kick off setting their volume and mute state
targets.forEach(function(target) {
    participantSpecificMsg = {}
    participantSpecificMsg.playerName = target.player_name
    participantSpecificMsg.payload = target.volume
    if ("leave_muted_if" in target) {
        participantSpecificMsg.leave_muted_if = target.leave_muted_if
    }
    node.send(participantSpecificMsg);
});

return null

stopMsg = {}
stopMsg.playerName = global.get("state").currentlyPlayingMusic.value.leadPlayerName

musicPlaybackType = global.get("state").musicPlaybackType.value

// If it's something other than none/nothing
if (musicPlaybackType) {
    return [ msg, stopMsg ]
}
// Otherwise just stop current playback
return [ null, stopMsg ]

// Only play music if someone is home
if (global.get("state").isAnyoneHome.value == false) {
    msg.payload = ""
    return msg
}
// If master is asleep, set to sleep
if (global.get("state").isMasterAsleep.value) {
    msg.payload = "sleep"
    return msg
}

dayPhase = global.get("state").dayPhase.value

// If it's day time
if (dayPhase == "day" || dayPhase == "morning") {
    // If what changed was someone waking up, kick off some music
    if (msg.topic == "isMasterAsleep" && msg.payload == false) {
        // Sunday override
        var date = new Date();
        var daynum = date.getDay();
        // If day is not Sunday
        if (daynum != 0) {
            msg.payload = "morning"
            return msg
        }
    }
    // If master isn't asleep then day starts
    if (global.get("state").isMasterAsleep.value == false) {
        msg.payload = "day"
        return msg
    }
// If it's sunset
} else if (dayPhase == "sunset"
        || dayPhase == "dusk") {
    msg.payload = "evening"
    return msg
} else if (dayPhase == "winddown"
        || dayPhase == "night") {
    msg.payload = "winddown"
    return msg
}

if (global.get("state").isAnyoneHome.value == false) {
    msg.payload = false
    return msg
}
// Somebody is home, do nothing
return null

playerNames = new Set()

musicConfig = global.get("state").musicConfig.value

Object.values(musicConfig).forEach(function(musicType) {
    musicType.participants.forEach(function(participant) {
        playerNames.add(participant.player_name)
    });
});

playerNames.forEach(function(playerName) {
    msg = {}
    msg.playerName = playerName
    node.send(msg);
});

msg.payload.forEach(function(participant) {
    msg.payload = null
    msg.playerName = participant.playerName
    if ([ "Living Room", "Guest Bathroom", "Guest Bedroom" ].indexOf(msg.playerName) > -1) {
        node.send(msg)
    }
});

// Does this participant even have this attribute?
if ("leave_muted_if" in msg) {
    // Iterate over all criteria
    for (const criteria of msg.leave_muted_if) {
        // Does the value of the variable match the criteria?
        if (global.get("state")[criteria.variable].value == criteria.value) {
            // Mute this player
            msg.payload = "on"
            return msg
        }
    }
}
// Unmute this player
msg.payload = "off"
return msg

// Get the participants in the currently playing music, which are our active players
activePlayers = global.get("state").currentlyPlayingMusic.value.participants

// For each of the active players, check if the changed variable is the condition
activePlayers.forEach(function(participant) {
    // Players may not even have a criteria
    if ("leave_muted_if" in participant) {
        // Iterate over all criteria
        participant.leave_muted_if.forEach(function(criteria) {
            // This criteria is the one that changed
            if (msg.topic == criteria.variable) {
                newMsg = {}
                newMsg.target = participant.player_name
                node.send(newMsg)
            }
        });
    }
});

return null

// If this was anything BUT the pooper's button
if(msg.payload.event.id != "poopers_button_button") {
    // Do nothing
    return null
}
// If this was anything BUT a short release
if(msg.payload.event.type != "short_release") {
