current_volume = msg.payload

desired_volume = msg.desired_volume

if (current_volume < desired_volume) {
    current_volume++
    msg.payload = current_volume
    node.send([msg, msg])
} else {
    msg.payload = desired_volume
    node.send([msg, null])
}

currentMusicConfig = global.get("state").musicConfig.value
newMusicConfig = msg.payload.music

if (Object.keys(newMusicConfig).length < Object.keys(currentMusicConfig).length) {
    node.error("New music config is less complex than current; refusing to set it")
    return null
}

msg.payload = newMusicConfig
return msg;

// If sunset
if (
    msg.payload.sunevent == "goldenHour" ||
    msg.payload.sunevent == "sunsetStart" ||
    msg.payload.sunevent == "sunset"
  ) {
    // output true
    msg.payload = "sunset"
}
// If dusk
else if (
    msg.payload.sunevent == "dusk" ||
    msg.payload.sunevent == "nauticalDusk"
  ) {
    // output true
    msg.payload = "dusk"
}
// If night
else if (
    msg.payload.sunevent == "night" ||
    msg.payload.sunevent == "nightEnd" ||
    msg.payload.sunevent == "nauticalDawn" ||
    msg.payload.sunevent == "dawn" ||
    msg.payload.sunevent == "nadir"
  ) {
    // output true
    msg.payload = "night"
}
// If morning
else if (
    msg.payload.sunevent == "sunrise" ||
    msg.payload.sunevent == "sunriseEnd" ||
    msg.payload.sunevent == "goldenHourEnd"
  ) {
    // output true
    msg.payload = "morning"
}
// If it's not morning, sunset, dusk, or night; assume it's day
else {
    msg.payload = "day"
}

// Check if this is new, if it is send it
if (global.get("state").sunevent.value != msg.payload) {
    return msg
}
return null

sun_event = global.get("state").sunevent.value

msg = {}

// No override of morning, just go with the sun event
if (sun_event == "morning")
    msg.payload = "morning"
// No override of day, just go with the sun event
if (sun_event == "day")
    msg.payload = "day"
// No override of sunset, just go with the sun event
if (sun_event == "sunset")
    msg.payload = "sunset"
// If it's dusk we're going to override
if (sun_event == "dusk") {
    // Get current time
    now = new Date()
    // Get the hour
    this_hour = now.getHours()
    // If it's after 7pm, go ahead and roll with dusk
    if (this_hour >= 19) {
        msg.payload = "dusk"
    // Otherwise, freeze us at sunset
    } else {
        msg.payload = "sunset"
    }
}
// If it's night we're going to override
if (sun_event == "night") {
    // Get current time
    now = new Date()
    // Get the hour
    this_hour = now.getHours()
    // Get the minute
    this_minute = now.getMinutes()
    // If it's after 11:30pm, go ahead and roll with night
    if (this_hour >= 23 && this_minute >= 30) {
        msg.payload = "night"
    // If it's after 9:30pm, go ahead and roll with winddown
    } else if ((this_hour >= 21 && this_minute >= 30) || this_hour >= 22) {
        msg.payload = "winddown"
    // If it's after 7pm, use dusk instead
    } else if (this_hour >= 19) {
        msg.payload = "dusk"
    } else {
    // If it's not 7pm yet I don't care, use sunset
        msg.payload = "sunset"
    }
}

return msg;

