    endOfTomorrowMorning > msg.next_event_start) {
        return msg;
}

event_start = new Date(parseInt(msg.next_event_start))

hour = event_start.getHours();
minute = event_start.getMinutes();

notification_message = msg.person + ", you have an event on your " + msg.calendar + " calendar which starts at "

if (minute == 0) {
    notification_message = notification_message + hour + " am"
} else {
    notification_message = notification_message + hour + " " + minute + " am"
}

notification_message = notification_message + " tomorrow morning."

