const event = new Date(parseInt(msg.next_event_start))

time_until_event_start = event - now

// Calculate ten hours
ten_hours_in_ms = (1000 * 60 * 60 * 11.5)

if (time_until_event_start < ten_hours_in_ms) {
    return msg;
