func:
// If either of us are home, someone is home; otherwise neither of us are home
msg.payload = global.get("state").isNickHome.value || global.get("state").isCarolineHome.value
return msg;

// If everyone is asleep true otherwise false
msg.payload = global.get("state").isMasterAsleep.value && global.get("state").isGuestAsleep.value
return msg;

// Check if anyone is home
if(global.get("state").isAnyoneHome.value == false) {
    return null
}
// Check if guests currently considered asleep
if(global.get("state").isGuestAsleep.value) {
    // They are, nothing to change here
    return null
}
// Do we even have a guest right now?
if(global.get("state").isHaveGuests.value == false) {
    // Nope! stop doing this
    return null
}
// Didn't disqualify this, it's what we were waiting for
return msg

// If we don't have guests, their asleep status should match master
if (global.get("state").isHaveGuests.value == false) {
    msg.payload = global.get("state").isMasterAsleep.value
    return msg;
}

// Check if anyone is home
if(global.get("state").isAnyoneHome.value == false) {
    return null
}
// Check if guests currently considered asleep
if(global.get("state").isMasterAsleep.value) {
    // They are, nothing to change here
    return null
}
// Didn't disqualify this, it's what we were waiting for
return msg

// If anyone is asleep true otherwise false
msg.payload = global.get("state").isMasterAsleep.value || global.get("state").isGuestAsleep.value
return msg;

// If sunset
if (
    msg.payload.sunevent == "goldenHour" ||
    msg.payload.sunevent == "sunsetStart" ||
    msg.payload.sunevent == "sunset"
  ) {
    // output true
    msg.payload = "sunset"
}
// If dusk
else if (
    msg.payload.sunevent == "dusk" ||
    msg.payload.sunevent == "nauticalDusk"
  ) {
    // output true
    msg.payload = "dusk"
}
// If night
else if (
    msg.payload.sunevent == "night" ||
    msg.payload.sunevent == "nightEnd" ||
    msg.payload.sunevent == "nauticalDawn" ||
    msg.payload.sunevent == "dawn" ||
    msg.payload.sunevent == "nadir"
  ) {
    // output true
    msg.payload = "night"
}
// If morning
else if (
    msg.payload.sunevent == "sunrise" ||
    msg.payload.sunevent == "sunriseEnd" ||
    msg.payload.sunevent == "goldenHourEnd"
  ) {
    // output true
    msg.payload = "morning"
}
// If it's not morning, sunset, dusk, or night; assume it's day
else {
    msg.payload = "day"
}

// Check if this is new, if it is send it
if (global.get("state").sunevent.value != msg.payload) {
    return msg
}
return null

sun_event = global.get("state").sunevent.value

msg = {}

// Get current time
now = new Date()
// Get the hour
this_hour = now.getHours()
// Get the minute
this_minute = now.getMinutes()

// Get override times
dusk_time = new Date(global.get("state").schedule.value.dusk)
winddown_time = new Date(global.get("state").schedule.value.winddown)
night_time = new Date(global.get("state").schedule.value.night)

// No override of morning, just go with the sun event
if (sun_event == "morning")
    // Override if after noon
    if (this_hour >= 12) {
        msg.payload = "day"
    } else {
        msg.payload = "morning"
    }
// No override of day, just go with the sun event
if (sun_event == "day")
    msg.payload = "day"
// No override of sunset, just go with the sun event
if (sun_event == "sunset")
    msg.payload = "sunset"
// If it's dusk we're going to override
if (sun_event == "dusk") {
    // If it's after the dusk override time go with dusk
    if (now > dusk_time) {
        msg.payload = "dusk"
    // Otherwise, freeze us at sunset
    } else {
        msg.payload = "sunset"
    }
}
// If it's night we're going to override
if (sun_event == "night") {
    // If it's after night time
    if (now > night_time) {
        msg.payload = "night"
    // If it's after winddown time
    } else if (now > winddown_time) {
        msg.payload = "winddown"
    // If it's after dusk time
    } else if (now > dusk_time) {
        msg.payload = "dusk"
    } else {
    // If it's not dusk time yet, just stick with sunset
        msg.payload = "sunset"
    }
}

return msg;

var today = new Date();
var day_of_week = today.getDay();
var dd = String(today.getDate()).padStart(2, '0');
var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
var yyyy = today.getFullYear();

today = yyyy + '-' + mm + '-' + dd;

schedule = {}

function get_time_from_string (string_time) {
    return Date.parse(today+'T'+string_time+":00")
}

// Get the schedule for today's day of the week
today_schedule = msg.payload.schedule[day_of_week]

schedule.begin_wake = get_time_from_string(today_schedule.begin_wake)
schedule.wake = get_time_from_string(today_schedule.wake)
schedule.dusk = get_time_from_string(today_schedule.dusk)
schedule.winddown = get_time_from_string(today_schedule.winddown)
schedule.stop_screens = get_time_from_string(today_schedule.stop_screens)
schedule.go_to_bed = get_time_from_string(today_schedule.go_to_bed)
schedule.night = get_time_from_string(today_schedule.night)

msg.payload = schedule

return msg;

const today = new Date()
const tomorrow = new Date(today)
tomorrow.setDate(tomorrow.getDate() + 1)

startOfTomorrowMorning = new Date(tomorrow);
startOfTomorrowMorning.setHours(4);
startOfTomorrowMorning.setMinutes(0);
startOfTomorrowMorning.setSeconds(0);

endOfTomorrowMorning = new Date(tomorrow);
endOfTomorrowMorning.setHours(10);
endOfTomorrowMorning.setMinutes(0);
endOfTomorrowMorning.setSeconds(0);

if (startOfTomorrowMorning < msg.next_event_start &&
    endOfTomorrowMorning > msg.next_event_start) {
        return msg;
}

event_start = new Date(parseInt(msg.next_event_start))

hour = event_start.getHours();
minute = event_start.getMinutes();

notification_message = msg.person + ", you have an event on your " + msg.calendar + " calendar which starts at "

if (minute == 0) {
    notification_message = notification_message + hour + " am"
} else {
    notification_message = notification_message + hour + " " + minute + " am"
}

notification_message = notification_message + " tomorrow morning."

msg.payload = notification_message

return msg;

if (global.get("state").isMasterAsleep.value == false) {
    return msg
}

if (global.get("state").isNickHome.value) {
    return msg
}

if (global.get("state").isCarolineHome.value) {
    return msg
}

if (global.get("state").isAnyoneHome.value == false) {
    return null
}
if (global.get("state").isEveryoneAsleep.value == true) {
    return null
}
return msg;

hueConfig = global.get("state").hueConfig.value

dayPhase = global.get("state").dayPhase.value

hueConfig.rooms.forEach(function(thisRoomToActivate) {
    // Does this participant even have this attribute?
    if ("off_if_true" in thisRoomToActivate) {
        // If so, is it true?
        if (global.get("state")[thisRoomToActivate.off_if_true].value) {
            return null
        }
    }

    msg.payload = {}
    msg.payload.group_name = thisRoomToActivate.hue_group
    msg.payload.scene_name = dayPhase
    msg.payload.dynamic = true
    // The nook doesn't do well with dynamics b/c of its lights
    if (thisRoomToActivate.hue_group == "Nook") {
        msg.payload.dynamic = false
    }
    node.send(msg)
});

hueConfig = global.get("state").hueConfig.value

dayPhase = global.get("state").dayPhase.value

hueConfig.rooms.forEach(function(thisRoomToTurnOff) {
    // Does this participant even have this attribute?
    if ("off_if_true" in thisRoomToTurnOff) {
        // Is the variable change triggering us the special "reset" case, or is it the variable this room cares about?
        if ((msg.topic == "reset" || msg.topic == thisRoomToTurnOff.off_if_true) 
        // Is the variable this room cares about true?
        && global.get("state")[thisRoomToTurnOff.off_if_true].value) {
            msg.targetArea = thisRoomToTurnOff.hass_area_id
            node.send(msg)
        }
    }
});

hueConfig = global.get("state").hueConfig.value

hueConfig.rooms.forEach(function(thisRoomToTurnOff) {
    // Pick every single room
    msg.targetArea = thisRoomToTurnOff.hass_area_id
    node.send(msg)
});

hueConfig = global.get("state").hueConfig.value

dayPhase = global.get("state").dayPhase.value

hueConfig.rooms.forEach(function(thisRoomToActivate) {
    // Does this participant even have this attribute?
    if ("off_if_true" in thisRoomToActivate) {
        // If so, is it true?
        if (global.get("state")[thisRoomToActivate.off_if_true].value) {
            return null
        }
    }

    msg.payload = {}
    msg.payload.group_name = thisRoomToActivate.hue_group
    msg.payload.scene_name = "Bright"
    msg.payload.dynamic = true
    // The nook doesn't do well with dynamics b/c of its lights
    if (thisRoomToActivate.hue_group == "Nook") {
        msg.payload.dynamic = false
    }
    node.send(msg)
});

dayPhase = global.get("state").dayPhase.value

if (dayPhase == "morning") {
    return msg;
}

// Music type is set by variable
musicType = global.get("state").musicPlaybackType.value
// We use an object variable or tracking which # playlist in the type
if (typeof global.get("state").musicPlaylistNumbers != "undefined") {
    musicPlaylistNumbers = global.get("state").musicPlaylistNumbers.value
} else {
    musicPlaylistNumbers = {}
}
// If this type has a number already, use it; otherwise create it as 0
if (musicType in musicPlaylistNumbers) {
    thisPlaylistNumber = musicPlaylistNumbers[musicType]
} else {
    thisPlaylistNumber = musicPlaylistNumbers[musicType] = 0
}

// Get full musicConfig
musicConfig = global.get("state").musicConfig.value.music
// Get the overall object for this type of music
musicTypeObject = musicConfig[musicType]
// Get the specifics for this playback option of the music type
musicToPlay = musicTypeObject["playback_options"][thisPlaylistNumber]
// Generate a list of players to group for playback based on the config
players = ""
// Blanket copy the music type's definition of participants onto the individual playback option
musicToPlay["participants"] = musicTypeObject["participants"]
// Iterate over all participants in this music playback type
musicTypeObject["participants"].forEach(function(participant, index) {
    // Take the name of this player participants and add it to the list of all participants
    players = players + participant.player_name + ","
    // The specific playback option's volume varies based on a multiplier
    musicToPlay["participants"].forEach(function(playbackParticipant) {
        if (playbackParticipant.player_name == participant.player_name) {
            // Determine appropriate volume for this participant using:
            //  * default from playback type for this speaker
            //  * multiplier for this playback option
            playbackParticipant.volume = Math.round(participant.default_volume * musicToPlay.volume_multiplier)
        }
    });
    // Mute all players being engaged in this group
    muteMsg = {}
    muteMsg.payload = "on"
    muteMsg.playerName = participant.player_name
    node.send([null, null, muteMsg, null])
});
// Strip last comma
players = players.replace(/,$/, ''); 

// Get lead player
leadPlayerName = musicToPlay.participants[0].player_name

// Playback is initiated by building the group and providing instructions to lead player
playMsg = {}
playMsg.payload = players
playMsg.playerName = leadPlayerName
playMsg.command = musicToPlay.command 
playMsg.uri = musicToPlay.uri

currentlyPlayingMusic = {}
currentlyPlayingMusic.payload = musicToPlay
currentlyPlayingMusic.payload.leadPlayer = leadPlayerName

// Need a new playlist numbers object
incrementMsg = {}
// If this playlist number for the given type of music would exceed the number of options, roll it over
if ((musicPlaylistNumbers[musicType] + 1) >= musicConfig[musicType]["playback_options"].length) {
    musicPlaylistNumbers[musicType] = 0
} else {
    // Otherwise increment it
    musicPlaylistNumbers[musicType] = musicPlaylistNumbers[musicType] + 1
}
incrementMsg.payload = musicPlaylistNumbers

return [ playMsg, currentlyPlayingMusic, null, incrementMsg ]

// By default, move to set volume and appropriately unmute all players in current playback
targets = global.get("state").currentlyPlayingMusic.value.participants
// If we were given a single specific target though, downscope
if ("target" in msg) {
    specificTarget = []
    targets.forEach(function(participant) {
        if (participant.player_name == msg.target) {
            specificTarget = [participant]
        }
    });
    if (specificTarget.length == 0) {
        node.warn("Asked to reset volume and mute state of unrecognized player: " + msg.target)
        return null
    }
    targets = specificTarget
}

// Iterate over our targets and kick off setting their volume and mute state
targets.forEach(function(target) {
    participantSpecificMsg = {}
    participantSpecificMsg.playerName = target.player_name
    participantSpecificMsg.payload = target.volume
    if ("leave_muted_if" in target) {
        participantSpecificMsg.leave_muted_if = target.leave_muted_if
    }
    node.send(participantSpecificMsg);
});

return null

stopMsg = {}
stopMsg.playerName = global.get("state").currentlyPlayingMusic.value.leadPlayerName

musicPlaybackType = global.get("state").musicPlaybackType.value

// If it's something other than none/nothing
if (musicPlaybackType) {
    return [ msg, stopMsg ]
}
// Otherwise just stop current playback
return [ null, stopMsg ]

// Only play music if someone is home
if (global.get("state").isAnyoneHome.value == false) {
    msg.payload = ""
    return msg
}
// If anyone is asleep, set to sleep
if (global.get("state").isAnyoneAsleep.value) {
    msg.payload = "sleep"
    return msg
}

dayPhase = global.get("state").dayPhase.value

// If it's day time
if (dayPhase == "day" || dayPhase == "morning") {
    // If what changed was the last person waking up, kick off some music
    if (msg.topic == "isAnyoneAsleep" && msg.payload == false) {
        // Sunday override
        var date = new Date();
        var daynum = date.getDay();
        // If day is not Sunday
        if (daynum != 0) {
            msg.payload = "morning"
            return msg
        }
    }
    // If noone is asleep then day starts
    if (global.get("state").isAnyoneAsleep.value == false) {
        msg.payload = "day"
        return msg
    }
// If it's sunset
} else if (dayPhase == "sunset"
        || dayPhase == "dusk") {
    msg.payload = "evening"
    return msg
} else if (dayPhase == "winddown"
        || dayPhase == "night") {
    // Override for when sleep sounds get started a little early
    if (global.get("state").musicPlaybackType.value == "sleep") {
        return null
    }
    msg.payload = "winddown"
    return msg
}

playerNames = new Set()

musicConfig = global.get("state").musicConfig.value.music

Object.values(musicConfig).forEach(function(musicType) {
    musicType.participants.forEach(function(participant) {
        playerNames.add(participant.player_name)
    });
});

playerNames.forEach(function(playerName) {
    msg = {}
    msg.playerName = playerName
    node.send(msg);
});

msg.payload.forEach(function(participant) {
    msg.payload = null
    msg.playerName = participant.playerName
    if ([ "Living Room", "Guest Bathroom", "Guest Bedroom" ].indexOf(msg.playerName) > -1) {
        node.send(msg)
    }
});

// Does this participant even have this attribute?
if ("leave_muted_if" in msg) {
    // Iterate over all criteria
    for (const criteria of msg.leave_muted_if) {
        // Does the value of the variable match the criteria?
        if (global.get("state")[criteria.variable].value == criteria.value) {
            // Mute this player
            msg.payload = "on"
            return msg
        }
    }
}
// Unmute this player
msg.payload = "off"
return msg

// Get the participants in the currently playing music, which are our active players
activePlayers = global.get("state").currentlyPlayingMusic.value.participants

// For each of the active players, check if the changed variable is the condition
activePlayers.forEach(function(participant) {
    // Players may not even have a criteria
    if ("leave_muted_if" in participant) {
        // Iterate over all criteria
        participant.leave_muted_if.forEach(function(criteria) {
            // This criteria is the one that changed
            if (msg.topic == criteria.variable) {
                newMsg = {}
                newMsg.target = participant.player_name
                node.send(newMsg)
            }
        });
    }
});

return null

// If this was anything BUT the pooper's button
if(msg.payload.event.id != "poopers_button_button") {
    // Do nothing
    return null
}
// If this was anything BUT a short release
if(msg.payload.event.type != "short_release") {
    // Do nothing
    return null
}

// Ok, it was a short release of the pooper's button
return msg

current_volume = msg.payload

desired_volume = msg.desired_volume

if (current_volume < desired_volume) {
    current_volume++
    msg.payload = current_volume
    msg.last_volume_set = current_volume
    node.send([msg, msg])
} else {
    msg.payload = desired_volume
    node.send([msg, null])
}

// What is the volume currently?
current_volume = msg.payload

// Check for someone fighting the system
if (current_volume < msg.last_volume_set) {
    // If the volume is less than what was last set, assume someone
    // is fighting the system trying to turn down the volume
    return null
}

// What is the volume supposed to be?
desired_volume = msg.desired_volume

// If we haven't gotten up to the desired volume
if (current_volume < desired_volume) {
    // The new set volume is current + 1
    new_volume = current_volume + 1
    // This new volume is the payload
    msg.payload = new_volume
    // Separately save off the value we're setting
    msg.last_volume_set = new_volume
    // Set the delay for the next turn up to be greater if volume is lower
    msg.delay = (100 - current_volume) * 1000
    // Send the change
    node.send(msg)
}

// We're done, everything is as it should be

playerNames = new Set()

musicConfig = global.get("state").musicConfig.value

Object.values(musicConfig).forEach(function(musicType) {
    musicType.participants.forEach(function(participant) {
        playerNames.add(participant.player_name)
    });
});

playerNames.forEach(function(playerName) {
    msg = {}
    msg.playerName = playerName
    msg.payload = 0
    node.send(msg);
});

if (global.get("state").isAnyoneHome.value == false) {
    return msg
}
// Somebody is home, do nothing
return null

msg = {}
msg.payload = Date.now()
return msg

lastVaccuumRunTimestamp = global.get("state").lastVacuumingTimestamp.value

timeSinceLastRun = Date.now() - lastVaccuumRunTimestamp

// Check against constant threshold in seconds
// 39600000 == 11 hours
if (timeSinceLastRun > 39600000) {
    // It's been too long
    return msg
}

if (global.get("state").isAnyoneHome.value == true) {
    return msg
}
// Somebody is home, do nothing
return null

// If nobody's home just go for it
if (global.get("state").isAnyoneHome.value == false) {
    return msg
}

// If we have guests, bail out
if (global.get("state").isHaveGuests.value == true) {
    return null;
}

// If we have master is not asleep, bail out
if (global.get("state").isMasterAsleep.value == false) {
    return null;
}

// Either nobody's here, or if they are it's just master and NO guests

return msg

if (global.get("state").isEveryoneAsleep.value == false) {
    return msg
}
// Somebody is home, do nothing
return null

if (global.get("state").isMasterAsleep.value == true) {
    return null
}
if (global.get("state").isGuestAsleep.value == true) {
    return null
}
if (global.get("state").isGuestBedroomDoorOpen.value == true) {
    return null
}

// don't run this if we have guests b/c the signal will be less reliable
if (global.get("state").isHaveGuests.value == true) {
    return null
}
// Only run this during the day
if (global.get("state").dayPhase.value != "day") {
    return null
}
// Onl run this if Nick is gone
if (global.get("state").isNickHome.value == true) {
    return null
}

msg.payload = {"On": true}

return msg

if (global.get("state").isAnyoneHome.value == false) {
    return null
}
if (global.get("state").isMasterAsleep.value == false) {
    return null
}
if (global.get("state").musicPlaybackType.value != "sleep") {
    return null
}
return msg;

begin_wake_time = new Date(global.get("state").schedule.value.begin_wake)
wake_time = new Date(global.get("state").schedule.value.wake)
stop_screens_time = new Date(global.get("state").schedule.value.stop_screens)
go_to_bed_time = new Date(global.get("state").schedule.value.go_to_bed)

now = new Date()

var ONE_HOUR = 60 * 60 * 1000; /* ms */

if (now > begin_wake_time) {
    // Only act if it's been true for less than one hour
    if ((now - begin_wake_time) < ONE_HOUR) {
        msg.topic = "begin_wake"
        node.send(msg)
    }
}

if (now > wake_time) {
    // Only act if it's been true for less than one hour
    if ((now - wake_time) < ONE_HOUR) {
        msg.topic = "wake"
        node.send(msg)
    }
}

if (now > stop_screens_time) {
    // Only act if it's been true for less than one hour
    if ((now - stop_screens_time) < ONE_HOUR) {
        msg.topic = "stop_screens"
        node.send(msg)
    }
}

if (now > go_to_bed_time) {
    // Only act if it's been true for less than one hour
    if ((now - go_to_bed_time) < ONE_HOUR) {
        msg.topic = "go_to_bed"
        node.send(msg)
    }
}

if (global.get("state").isAnyoneHome.value == false) {
    return null
}
if (global.get("state").isMasterAsleep.value == false) {
    return null
}

msg.payload = false

return msg;

if (global.get("state").isAnyoneHome.value == false) {
    return null
}
if (global.get("state").isEveryoneAsleep.value == true) {
    return null
}
return msg;

if (global.get("state").isAnyoneHome.value == false) {
    return null
}
if (global.get("state").isMasterAsleep.value == false) {
    return null
}
return msg;

// By default, move to set volume and appropriately unmute all players in current playback
speakers = global.get("state").currentlyPlayingMusic.value.participants
// Iterate over our targets and kick off setting their volume and mute state
speakers.forEach(function(target) {
    participantSpecificMsg = {}
    // If this player is in Master suite
    if (target.player_name.indexOf("Master") > -1) {
        participantSpecificMsg.playerName = target.player_name
        node.send(participantSpecificMsg);
    }
});

return null

observed_volume = msg.payload
msg.last_volume_set = observed_volume

currentKnownPlayingMusic = global.get("state").currentlyPlayingMusic.value

currentKnownPlayingMusic.participants.forEach(function(participant) {
    if (participant.player_name == msg.playerName) {
        participant.volume = observed_volume
    }
});

updateCurrentlyPlayingMusicMsg = {}
updateCurrentlyPlayingMusicMsg.payload = currentKnownPlayingMusic

return [updateCurrentlyPlayingMusicMsg, msg];

// What is the volume currently?
current_volume = msg.payload

// Check for someone fighting the system
if (current_volume > msg.last_volume_set) {
    // If the volume is greater than what was last set, assume someone
    // is fighting the system trying to keep the volume up
    currentlyPlayingMusic = global.get("state").currentlyPlayingMusic.value

    currentlyPlayingMusic.participants.forEach(function(participant) {
        // If it's this flow's speaker
        if (participant.player_name == msg.playerName) {
            participant.volume = current_volume
        }
    });
    msg.payload = currentlyPlayingMusic

    return [msg, null]
}

// What is the volume supposed to be?
desired_volume = 0

// If we haven't gotten down to the desired volume
if (current_volume > desired_volume) {
    currentlyPlayingMusic = global.get("state").currentlyPlayingMusic.value

    currentlyPlayingMusic.participants.forEach(function(participant) {
        // If it's this flow's speaker
        if (participant.player_name == msg.playerName) {
            participant.volume = current_volume
            // Reduce its volume by 1
            participant.volume--;
            msg.last_volume_set = participant.volume
        }
    });
    msg.payload = currentlyPlayingMusic

    // Set the delay for the next turn down to be greater if volume is lower
    msg.delay = (60 - current_volume) * 1000
    return [msg, msg]
}

// We're done, everything is as it should be

value = msg.payload

msg.payload = {}

msg.payload.variable = msg.topic

if (typeof value == "object") {
    msg.payload.object = value
    msg.payload.type = "object"
    msg.payload.object_string = JSON.stringify(msg.payload.object);
} else if (typeof value == "string") {
    msg.payload.string = value
    msg.payload.type = "string"
} else if (typeof value == "number") {
    msg.payload.number = value
    msg.payload.type = "number"
