var sun_event = global.get("state").sunevent.value

msg = {}

// Get current time
var now = new Date()
// Get the hour
var this_hour = now.getHours()
// Get the minute
var this_minute = now.getMinutes()

// Get override times
var dusk_time = new Date(global.get("state").schedule.value.dusk)
var winddown_time = new Date(global.get("state").schedule.value.winddown)
var night_time = new Date(global.get("state").schedule.value.night)

// No override of morning, just go with the sun event
if (sun_event == "morning")
    // Override if after noon
    if (this_hour >= 12) {
        msg.payload = "day"
    } else {
        msg.payload = "morning"
    }
// No override of day, just go with the sun event
if (sun_event == "day")
    msg.payload = "day"
// No override of sunset, just go with the sun event
if (sun_event == "sunset")
    msg.payload = "sunset"
// If it's dusk we're going to override
if (sun_event == "dusk") {
    msg.payload = "dusk"
}
// If it's night we're going to override
if (sun_event == "night") {
    // If it's after night time or early in the morning
    if (now > night_time || this_hour < 6) {
        msg.payload = "night"
    // If it's not night it's winddown
    } else {
        msg.payload = "winddown"
    }
}

return msg;

var today = new Date();
var day_of_week = today.getDay();
var dd = String(today.getDate()).padStart(2, '0');
var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
var yyyy = today.getFullYear();

today = yyyy + '-' + mm + '-' + dd;

var schedule = {}

function get_time_from_string (string_time) {
    var time
    try {
        time = Date.parse(today + 'T' + string_time + ":00")
        if (isNaN(time)) {
            throw new Error('Didnt parse time successfully');
        }
    } catch {
        time = Date.parse(today + 'T0' + string_time + ":00")
        if (isNaN(time)) {
            throw new Error('Didnt parse time successfully');
        }
    }
    console.debug(`Parsed time is ${time} from config value ${string_time}`)
    return time
}

// Get the schedule for today's day of the week
var today_schedule = msg.payload.schedule[day_of_week]

schedule.begin_wake = get_time_from_string(today_schedule.begin_wake)
schedule.wake = get_time_from_string(today_schedule.wake)
schedule.dusk = get_time_from_string(today_schedule.dusk)
schedule.winddown = get_time_from_string(today_schedule.winddown)
schedule.stop_screens = get_time_from_string(today_schedule.stop_screens)
schedule.go_to_bed = get_time_from_string(today_schedule.go_to_bed)
schedule.night = get_time_from_string(today_schedule.night)

msg.payload = schedule

return msg;

function make_volume_multiplier_float(object) {
  Object.keys(object).forEach(k => {
    if (typeof object[k] === 'object') {
      return make_volume_multiplier_float(object[k]);
    }

    if (k == "volume_multiplier") {
        object[k] = (object[k]).toFixed(2);
    }
  });

  return object;
}

msg.payload = make_volume_multiplier_float(msg.payload)

return msg;

