const toSnakeCase = str =>
    str &&
    str
        .match(/[A-Z]{2,}(?=[A-Z][a-z]+[0-9]*|\b)|[A-Z]?[a-z]+[0-9]*|[A-Z]|[0-9]+/g)
        .map(x => x.toLowerCase())
        .join('_');

var hueConfig = global.get("state").hueConfig.value
var dayPhase = global.get("state").dayPhase.value

hueConfig.rooms.forEach(function(thisRoom) {
    let shouldActivateScene = false;
    let shouldTurnOff = false;

    // Helper function to check if a variable list matches current topic or reset
    function isTopicRelevant(variableList) {
        if (msg.topic == "reset" || msg.topic == "dayPhase") return true;
        if (Array.isArray(variableList)) {
            return variableList.includes(msg.topic);
        } else {
            return msg.topic == variableList;
        }
    }

    // Helper function to evaluate conditions
    function evaluateConditions(variableList, expectedValue) {
        if (!variableList) return false;

        const variables = Array.isArray(variableList) ? variableList : [variableList];

        return variables.some(variable => {
            const stateValue = global.get("state")[variable]?.value;
            return stateValue === expectedValue;
        });
    }

    // Check if room should be turned ON based on conditions
    if ("on_if_true" in thisRoom) {
        if (isTopicRelevant(thisRoom.on_if_true) && evaluateConditions(thisRoom.on_if_true, true)) {
            shouldActivateScene = true;
        }
    }

    if ("on_if_false" in thisRoom) {
        if (isTopicRelevant(thisRoom.on_if_false) && evaluateConditions(thisRoom.on_if_false, false)) {
            shouldActivateScene = true;
        }
    }

    // Check if room should be turned OFF based on conditions
    if ("off_if_true" in thisRoom) {
        if (isTopicRelevant(thisRoom.off_if_true) && evaluateConditions(thisRoom.off_if_true, true)) {
            shouldTurnOff = true;
        }
    }

    if ("off_if_false" in thisRoom) {
        if (isTopicRelevant(thisRoom.off_if_false) && evaluateConditions(thisRoom.off_if_false, false)) {
            shouldTurnOff = true;
        }
    }

    // Generate output message based on determined action
    // ON takes precedence over OFF
    if (shouldActivateScene) {
        // Apply the appropriate scene for current day phase
        msg.payload = {}
        var scene_name = thisRoom.hue_group + " " + dayPhase 
        msg.entity = "scene." + String(toSnakeCase(scene_name))
        msg.payload.area_id = thisRoom.hass_area_id
        msg.payload.transition = thisRoom.transition_seconds
        msg.delay = msg.payload.transition * 1000
        msg.action = "scene"
        msg.config = thisRoom

        // The nook doesn't do well with dynamics b/c of its lights
        if (thisRoom.hue_group == "Nook") {
            msg.payload.dynamic = false
        }

        console.debug("Applying scene " + msg.entity + " to " + thisRoom.hue_group + 
                     (shouldTurnOff ? " (ON takes precedence over OFF)" : ""))
        node.send(msg)
    } else if (shouldTurnOff) {
        // Turn off the lights only if no ON conditions are met
        msg.payload = {}
        msg.payload.area_id = thisRoom.hass_area_id
        msg.action = "off"
        msg.config = thisRoom
        console.debug("Turning off lights in " + thisRoom.hue_group)
        node.send(msg)
    }
});

console.debug("Preparing for music playback")
// Music type is set by variable
var musicType = global.get("state").musicPlaybackType.value
// We use an object variable or tracking which # playlist in the type
var musicPlaylistNumbers = {}
if (typeof global.get("state").musicPlaylistNumbers != "undefined") {
    musicPlaylistNumbers = global.get("state").musicPlaylistNumbers.value
}
// If this type has a number already, use it; otherwise create it as 0
var thisPlaylistNumber
if (musicType in musicPlaylistNumbers) {
    thisPlaylistNumber = musicPlaylistNumbers[musicType]
} else {
    thisPlaylistNumber = musicPlaylistNumbers[musicType] = 0
}

// Get full musicConfig
var musicConfig = global.get("state").musicConfig.value.music
// Get the overall object for this type of music
var musicTypeObject = musicConfig[musicType]
// Get the specifics for this playback option of the music type
var musicToPlay = {}
if (thisPlaylistNumber in musicTypeObject["playback_options"]) {
    musicToPlay = musicTypeObject["playback_options"][thisPlaylistNumber]
} else {
    musicToPlay.volume_multiplier = 1
}
// Generate a comma-separate list of players to group for playback based on the config
var players = ""
// Generate an array list of players for preparation
var playerArray = []
// Blanket copy the music type's definition of participants onto the individual playback option
musicToPlay["participants"] = musicTypeObject["participants"]
console.debug("Collected all configuration needed for music playback")
// Iterate over all participants in this music playback type
musicTypeObject["participants"].forEach(function(participant, index) {
    console.debug("Processing " + participant.player_name)
    // Take the name of this player participants and add it to the list of all participants
    players = players + participant.player_name + ","
    // Add it to the array
    playerArray.push(participant.player_name)
    // The specific playback option's volume varies based on a multiplier
    musicToPlay["participants"].forEach(function(playbackParticipant) {
        if (playbackParticipant.player_name == participant.player_name) {
            // Determine appropriate volume for this participant using:
            //  * default from playback type for this speaker
            //  * multiplier for this playback option
            playbackParticipant.volume = Math.round(participant.base_volume * musicToPlay.volume_multiplier)
            playbackParticipant.default_volume = playbackParticipant.volume
            console.debug("Calculated volume of " + playbackParticipant.volume + " for " + participant.player_name)
        }
    });
    // Mute all players being engaged in this group
    var muteMsg = {}
    muteMsg.payload = "on"
    muteMsg.playerName = participant.player_name
    node.send([null, null, muteMsg, null])
});
// Strip last comma
players = players.replace(/,$/, ''); 

// Get lead player
var leadPlayerName = musicToPlay.participants[0].player_name

// Playback is initiated by building the group and providing instructions to lead player
var playMsg = {}
playMsg.payload = players
playMsg.playerArray = playerArray
playMsg.playerName = leadPlayerName
playMsg.entity_id = "media_player." + leadPlayerName.toLowerCase().replace(/\s+/g, '_');
playMsg.uri = musicToPlay.uri
playMsg.media_type = musicToPlay.media_type
console.debug("To play type " + musicType + " music, will play " + musicToPlay.uri)

var currentlyPlayingMusic = {}
currentlyPlayingMusic.payload = musicToPlay
currentlyPlayingMusic.payload.type = musicType
currentlyPlayingMusic.payload.leadPlayer = leadPlayerName

// Need a new playlist numbers object
var incrementMsg = {}
// If this playlist number for the given type of music would exceed the number of options, roll it over
if ((musicPlaylistNumbers[musicType] + 1) >= musicConfig[musicType]["playback_options"].length) {
    musicPlaylistNumbers[musicType] = 0
} else {
    // Otherwise increment it
    musicPlaylistNumbers[musicType] = musicPlaylistNumbers[musicType] + 1
}
incrementMsg.payload = musicPlaylistNumbers

// Build message to ensure full stoppage on engaged players
var stopMsg = {}
stopMsg.payload = playerArray

// playMsg kicks of buidling playback group and starting playback
// currentlyPlayingMusic just publishes/saves what we're going to play
// The null is the individual mute messages we already sent
// incrementMsg kicks the playlist number variable
// stopMsg is the list of players for stopping any exisitng playback
return [ playMsg, currentlyPlayingMusic, null, incrementMsg, stopMsg ]

// By default, move to set volume and appropriately unmute all players in current playback
var targets = global.get("state").currentlyPlayingMusic.value.participants
var type = global.get("state").currentlyPlayingMusic.value.type
// If we were given a single specific target though, downscope
if ("target" in msg) {
    console.debug("Setting volume and mute state for specific speaker: " + msg.target)
    var specificTarget = []
    targets.forEach(function(participant) {
        if (participant.player_name == msg.target) {
            specificTarget = [participant]
        }
    });
    if (specificTarget.length == 0) {
        node.warn("Asked to reset volume and mute state of unrecognized player: " + msg.target)
        return null
    }
    targets = specificTarget
} else {
    console.debug("Setting volume and mute state for all speakers")
}

// Iterate over our targets and kick off setting their volume and mute state
targets.forEach(function(target) {
    var participantSpecificMsg = {}
    participantSpecificMsg.playerName = target.player_name
    participantSpecificMsg.payload = target.volume
    if ("leave_muted_if" in target) {
        participantSpecificMsg.leave_muted_if = target.leave_muted_if
    }
    participantSpecificMsg.starting_type = type
    node.send(participantSpecificMsg);
});

return null

var musicPlaybackType = global.get("state").musicPlaybackType.value

// If it's something other than none/nothing
if (musicPlaybackType) {
    console.debug("Sending message to begin playback of " + musicPlaybackType)
    return [ msg, null ]
}
var blankMsg = {"payload": null}
// Otherwise just stop current playback
console.debug("Sending blank playback message")
return [null, blankMsg ]

var playerNames = new Set()

try {
    msg.payload.forEach(function(thisPlayerName) {
        playerNames.add(thisPlayerName)
    });
} catch (error) {
    var musicConfig = global.get("state").musicConfig.value.music

    Object.values(musicConfig).forEach(function (musicType) {
        musicType.participants.forEach(function (participant) {
            playerNames.add(participant.player_name)
        });
    });
}

playerNames.forEach(function(playerName) {
    msg = {}
    msg.playerName = playerName
    node.send(msg);
});

var currentKnownPlayingMusic = global.get("state").currentlyPlayingMusic.value

currentKnownPlayingMusic.participants.forEach(function (participant) {
    var target_speakers = ["Bedroom", "Kitchen"]
    if (target_speakers.includes(participant.player_name)) {
        participant.volume = participant.volume + 10
    }
});

msg.payload = currentKnownPlayingMusic
return msg

console.debug("Processing mute criteria for " + msg.playerName)
// Does this participant even have this attribute?
if ("leave_muted_if" in msg) {
    // Iterate over all criteria
    for (const criteria of msg.leave_muted_if) {
        // Does the value of the variable match the criteria?
        if (global.get("state")[criteria.variable].value == criteria.value) {
            // Mute this player
            msg.payload = "on"
            return msg
        }
    }
}
// Unmute this player
msg.payload = "off"
return msg

console.debug("Processing change in variable " + msg.topic + " for relavence to music playback")

// Get the participants in the currently playing music, which are our active players
var activePlayers = global.get("state").currentlyPlayingMusic.value.participants

// For each of the active players, check if the changed variable is the condition
activePlayers.forEach(function(participant) {
    // Players may not even have a criteria
    if ("leave_muted_if" in participant) {
        // Iterate over all criteria
        participant.leave_muted_if.forEach(function(criteria) {
            // This criteria is the one that changed
            if (msg.topic == criteria.variable) {
                console.debug(msg.topic + " is relavent to " + participant.player_name)
                var newMsg = {}
                newMsg.target = participant.player_name
                node.send(newMsg)
            }
        });
    }
});

return null

var current_volume = msg.payload

var desired_volume = msg.desired_volume

if (current_volume < desired_volume) {
    current_volume++
    msg.payload = current_volume
    msg.last_volume_set = current_volume
    node.send([msg, msg])
} else {
    msg.payload = desired_volume
    node.send([msg, null])
}

console.debug("Processing fade in for " + msg.playerName)

// What is the volume currently?
var current_volume = msg.payload

// Check for someone fighting the system
if (current_volume < msg.last_volume_set) {
    console.debug("Found someone fighting fade in for " + msg.playerName)
    // If the volume is less than what was last set, assume someone
    // is fighting the system trying to turn down the volume
    console.debug("Terminating fade in for " + msg.playerName)
    return null
}

// Check for a change in music playback type
var starting_type = msg.starting_type
var current_type = global.get("state").currentlyPlayingMusic.value.type
if (starting_type != current_type) {
    console.debug("Found that music playback type has changed for " + msg.playerName)
    // We have done weird things with volume by applying settings after the type has changed
    console.debug("Terminating fade in for " + msg.playerName)
    return null
}

// What is the volume supposed to be?
var allParticipantSpeakers = global.get("state").currentlyPlayingMusic.value.participants
allParticipantSpeakers.forEach(function (aParticipantSpeaker) {
    // If this is the config for the speaker we're setting volume for
    if (aParticipantSpeaker.player_name == msg.playerName) {
        // Read desired volume from this config, NOT what we were originally told
        var desired_volume = aParticipantSpeaker.volume
    }
});

if (typeof desired_volume === 'undefined') {
    var desired_volume = msg.desired_volume
}

// If we haven't gotten up to the desired volume
if (current_volume < desired_volume) {
    console.debug("Still need to turn up " + msg.playerName)
    console.debug("Current volume " + current_volume)
    console.debug("Desired volume " + desired_volume)
    // The new set volume is current + 1
    var new_volume = current_volume + 1
