function determineFanSpeedByHumidifier(airConditionData) {
  if (airConditionData.hasOwnProperty("humidity control")) {

    var desired_humidity = airConditionData["humidity control"]["desired humidity"]
    var current_humidity = airConditionData["humidity"]

