  determineFanSpeedByHumidifier(airConditionData)

  var actualFanSpeed = Math.max(...Object.values(airConditionData["considered fan speeds"]))

  node.debug("Decided on fan speed of " + actualFanSpeed + "%")

  airConditionData["fan speed"] = actualFanSpeed
}

