function determineOutputEnergyLevel(batteryEnergyLevel, solarPowerEnergyLevel, energyStateDefinitions) {
    // Extract the ordered list of condition names
    const levels = energyStateDefinitions.map(def => def.condition_name);

    // Get numeric indexes for easy comparison
    const batteryIndex = levels.indexOf(batteryEnergyLevel);
    const solarIndex = levels.indexOf(solarPowerEnergyLevel);

    // Find lowest and highest input levels
    const minIndex = Math.min(batteryIndex, solarIndex);
    const maxIndex = Math.max(batteryIndex, solarIndex);

    // Maximum output allowed is at most one higher than the lowest input level
    const maxAllowedIndex = Math.min(minIndex + 1, levels.length - 1);

    // Final output is the lesser of maxIndex and maxAllowedIndex
    const outputIndex = Math.min(maxIndex, maxAllowedIndex);

    return levels[outputIndex];
}

msg.payload = determineOutputEnergyLevel(batteryEnergyLevel, solarProductionEnergyLevel, energyStateDefinitions)

